/**
 * utils.js â€“ reusable helpers for Swag Labs tests
 */

/**
 * Perform login action on Swag Labs
 * @param {import('@playwright/test').Page} page
 * @param {string} username
 * @param {string} password
 */
export async function login(page, username, password) {
  await page.fill('[data-test="username"]', username);
  await page.fill('[data-test="password"]', password);
  await page.click('[data-test="login-button"]');
}

/**
 * Get the error message text if present
 * @param {import('@playwright/test').Page} page
 * @returns {Promise<string|null>}
 */
export async function getLoginErrorMessage(page) {
  const error = page.locator('[data-test="error"]');
  return (await error.count()) > 0 ? error.textContent() : null;
}

/**
 * Clear both login fields
 * @param {import('@playwright/test').Page} page
 */
export async function clearLoginFields(page) {
  await page.fill('[data-test="username"]', '');
  await page.fill('[data-test="password"]', '');
}

