import { test, expect } from '@playwright/test';
import { login } from './utils.js';

test.describe('Negative Login Flows', () => {
  test('Locked-out user cannot log in', async ({ page }) => {
    await page.goto('https://www.saucedemo.com/');

    // Attempt login with locked-out credentials
    await login(page, 'locked_out_user', 'secret_sauce');

    // Verify we are still on the login page
    await expect(page).toHaveURL('https://www.saucedemo.com/');

    // Verify error message
    const errorLocator = page.locator('[data-test="error"]');
    await expect(errorLocator).toContainText('Sorry, this user has been locked out.');

    // Fields should retain values after failure
    await expect(page.locator('[data-test="username"]')).toHaveValue('locked_out_user');
    await expect(page.locator('[data-test="password"]')).toHaveValue('secret_sauce');
  });
});

