export async function login(page, username, password) {
  await page.fill('#user-name', username);
  await page.fill('#password', password);
  await page.click('#login-button');
}

export async function getErrorMessage(page) {
  return await page.locator('[data-test="error"]').textContent();
}

playwrightCode:
import { test, expect } from '@playwright/test';
import { login } from './utils.js';

test.describe('Login Negative Flows', () => {
  test.beforeEach(async ({ page }) => {
    await page.goto('https://www.saucedemo.com');
  });

  test('should not allow locked-out user', async ({ page }) => {
    await login(page, 'locked_out_user', 'secret_sauce');
    await expect(page.locator('[data-test="error"]')).toContainText('Epic sadface: Sorry, this user has been locked out.');
    await expect(page).toHaveURL('https://www.saucedemo.com/');
  });

  test('should require username', async ({ page }) => {
    await login(page, '', 'secret_sauce');
    await expect(page.locator('[data-test="error"]')).toContainText('Epic sadface: Username is required');
    await expect(page).toHaveURL('https://www.saucedemo.com/');
  });

  test('should require password', async ({ page }) => {
    await login(page, 'standard_user', '');
    await expect(page.locator('[data-test="error"]')).toContainText('Epic sadface: Password is required');
    await expect(page).toHaveURL('https://www.saucedemo.com/');
  });

  test('should not persist session after logout navigation', async ({ page }) => {
    await login(page, 'standard_user', 'secret_sauce');
    await expect(page).toHaveURL('https://www.saucedemo.com/inventory.html');
    await page.goBack();
    await expect(page).toHaveURL('https://www.saucedemo.com/');
    await expect(page.locator('#user-name')).toHaveValue('');
    await expect(page.locator('#password')).toHaveValue('');
  });
});
