import { test, expect } from '@playwright/test';
import { fillInput, clickButton, getErrorMessage, isOnPage } from './utils.js';

test.describe('Login - Negative Path', () => {
  test('should NOT reach inventory page with invalid credentials', async ({ page }) => {
    await page.goto('https://www.saucedemo.com/');
    await expect(page).toHaveTitle(/Swag Labs/);

    await fillInput(page, 'input[placeholder="Username"]', 'standard_user');
    await fillInput(page, 'input[placeholder="Password"]', 'secret_sauce');
    await clickButton(page, 'input[type="submit"]');

    // Negative assertions
    const reachedInventory = await isOnPage(page, 'https://www.saucedemo.com/inventory.html');
    expect(reachedInventory).toBe(false);

    const errorText = await getErrorMessage(page);
    expect(errorText).toBeTruthy();

    await page.screenshot({ path: 'negative-login-fail.png' });
  });
});

