export async function login(page, username, password) {
  await page.fill('[data-test="username"]', username);
  await page.fill('[data-test="password"]', password);
  await page.click('[data-test="login-button"]');
}

export async function getInventoryItems(page) {
  return page.locator('.inventory_item');
}

playwrightCode:
import { test, expect } from '@playwright/test';
import { login } from './utils.js';

test.describe('Login - Negative Path Simulation', () => {
  test('should fail when invalid credentials are used', async ({ page }) => {
    await page.goto('https://www.saucedemo.com/');
    await expect(page).toHaveTitle(/Swag Labs/);

    // Intentionally use wrong credentials to trigger failure
    await login(page, 'wrong_user', 'wrong_password');

    // Verify error message appears
    const error = page.locator('[data-test="error"]');
    await expect(error).toBeVisible();
    await expect(error).toContainText('Username and password do not match');

    // Verify we are still on login page
    await expect(page).toHaveURL('https://www.saucedemo.com/');
  });
});
