/**
 * utils.js – reusable helper functions for Saucedemo pages
 */

/**
 * Log in with given credentials
 * @param {import('@playwright/test').Page} page
 * @param {string} username
 * @param {string} password
 */
export async function login(page, username, password) {
  await page.fill('[data-test="username"]', username);
  await page.fill('[data-test="password"]', password);
  await page.click('[data-test="login-button"]');
}

/**
 * Log out via burger menu
 * @param {import('@playwright/test').Page} page
 */
export async function logout(page) {
  await page.click('#react-burger-menu-btn');
  await page.click('[data-test="logout-sidebar-link"]');
}

/**
 * Return name and price text of the first inventory item
 * @param {import('@playwright/test').Page} page
 * @returns {Promise<{name:string,price:string}>}
 */
export async function getFirstInventoryItem(page) {
  const item = page.locator('.inventory_item').first();
  await item.waitFor({ state: 'visible' });
  const name = await item.locator('.inventory_item_name').innerText();
  const price = await item.locator('.inventory_item_price').innerText();
  return { name, price };
}

playwrightCode:
import { test, expect } from '@playwright/test';
import { login, logout, getFirstInventoryItem } from './utils.js';

test.describe('Inventory Item Validation', () => {
  test('First item should have title "Sauce Labs Backpack" and price "$29.99"', async ({ page }) => {
    await page.goto('https://www.saucedemo.com/');
    await login(page, 'standard_user', 'secret_sauce');

    // Wait for inventory list to render
    await page.waitForURL(/.*inventory\.html?/);
    await expect(page.locator('.inventory_list')).toBeVisible();

    const { name, price } = await getFirstInventoryItem(page);

    expect(name).toBe('Sauce Labs Backpack');
    expect(price).toBe('$29.99');

    // Optional cleanup
    await logout(page);
  });
});
