playwrightCode:
import { test, expect } from '@playwright/test';
import { login } from './utils.js';

test('Negative: login with invalid credentials should stay on login page', async ({ page }) => {
  await page.goto('https://www.saucedemo.com/');
  await login(page, 'invalid_user', 'wrong_password');
  await expect(page).toHaveURL(/.*saucedemo.com\/$/);
  await expect(page.locator('[data-test="error"]')).toContainText('Username and password do not match');
});

utilsCode:
export async function login(page, username, password) {
  await page.fill('input[placeholder="Username"]', username);
  await page.fill('input[placeholder="Password"]', password);
  await page.click('input[type="submit"]');
}

export async function assertErrorMessage(page, expectedText) {
  const errorLocator = page.locator('[data-test="error"]');
  await errorLocator.waitFor({ state: 'visible' });
  await expect(errorLocator).toHaveText(expectedText);
}

playwrightCode:
import { test, expect } from '@playwright/test';
import { login, assertErrorMessage } from './utils.js';

test('Locked user cannot log in and sees correct error', async ({ page }) => {
  await page.goto('https://www.saucedemo.com/');
  await login(page, 'locked_out_user', 'secret_sauce');
  await assertErrorMessage(page, 'Epic sadface: Sorry, this user has been locked out.');
  await expect(page).toHaveURL('https://www.saucedemo.com/');
});
