export async function fillText(page, selector, text) {
  await page.fill(selector, text);
}

export async function clickElement(page, selector) {
  await page.click(selector);
}

export async function assertUrl(page, expectedUrl) {
  await page.waitForURL(expectedUrl);
}

export async function assertText(page, selector, expectedText) {
  await page.waitForSelector(selector);
  const text = await page.textContent(selector);
  if (!text.includes(expectedText)) {
    throw new Error(`Expected text "${expectedText}" not found in "${text}"`);
  }
}

