import { test, expect } from '@playwright/test';
import { fillText, clickElement, assertUrl, assertText } from '../utils.js';

test('Test Login Successful', async ({ page }) => {
  await page.goto('https://www.saucedemo.com');
  await expect(page).toHaveTitle(/Swag Labs/);

  await fillText(page, 'input[placeholder="Username"]', 'standard_user');
  await fillText(page, 'input[placeholder="Password"]', 'secret_sauce');
  await clickElement(page, 'input[type="submit"]');

  await assertUrl(page, 'https://www.saucedemo.com/inventory.html');
  await assertText(page, 'span.title', 'Products');
});

