/**
 * Adds the first inventory item to the cart.
 * @param {import('@playwright/test').Page} page
 */
export async function addFirstItemToCart(page) {
  const addButton = page.locator('.inventory_item').first().locator('button:has-text("Add to cart")');
  await addButton.click();
  await expect(addButton).toHaveText('Remove');
}

/**
 * Clicks the shopping-cart icon to navigate to the cart page.
 * @param {import('@playwright/test').Page} page
 */
export async function goToCart(page) {
  await page.click('.shopping_cart_container');
  await expect(page).toHaveURL(/.*cart.html/);
}

/**
 * Returns the displayed price text for the nth cart item (0-based index).
 * @param {import('@playwright/test').Page} page
 * @param {number} index
 * @returns {Promise<string>}
 */
export async function getCartItemPrice(page, index = 0) {
  return page.locator('.cart_item').nth(index).locator('.inventory_item_price').textContent();
}

playwrightCode:
import { test, expect } from '@playwright/test';
import { addFirstItemToCart, goToCart, getCartItemPrice } from './utils.js';

test.describe('Add first item to checkout', () => {
  test('should add the first item and validate $29.99 in cart', async ({ page }) => {
    await page.goto('/inventory.html');

    // Add the first item to the cart
    await addFirstItemToCart(page);

    // Navigate to cart
    await goToCart(page);

    // Validate price
    const price = await getCartItemPrice(page, 0);
    expect(price).toBe('$29.99');
  });
});

