import { test, expect } from '@playwright/test';
import { login } from './utils.js';


test.describe("Check first item title and price after successful login", () => {
  test('Check first item title and price after successful login', async ({ page }) => {
    await page.goto('https://www.saucedemo.com/');
    await login(page, 'standard_user', 'secret_sauce');

    // Ensure we are on the inventory page
    await expect(page).toHaveURL('https://www.saucedemo.com/inventory.html');

    // Locate the first inventory item
    const firstItem = page.locator('.inventory_item').first();

    // Check title
    const title = await firstItem.locator('.inventory_item_name').textContent();
    expect(title.trim()).toBe('Sauce Labs Backpack');

    // Check price
    const price = await firstItem.locator('.inventory_item_price').textContent();
    expect(price.trim()).toBe('$29.99');
  });
});
