export async function addFirstItemByPrice(page, price) {
  const item = page.locator('[data-test="inventory-item"]')
                   .filter({ hasText: `$${price}` })
                   .first();
  await item.locator('[data-test="add-to-cart-btn"]').click();
}

export async function openCart(page) {
  await page.click('[data-test="shopping-cart-container"]');
  await page.waitForURL('**/cart.html');
}

export async function safeAddToCart(page, locator) {
  await locator.waitFor({ state: 'visible' });
  await locator.click();
  await locator.waitFor({ state: 'hidden' });
}

export async function cartTotal(page) {
  const text = await page.locator('[data-test="cart-total"]').innerText();
  return parseFloat(text.replace(/[^0-9.]/g, ''));
}

playwrightCode:
import { test, expect } from '@playwright/test';
import { addFirstItemByPrice, openCart, safeAddToCart, cartTotal } from './utils.js';

test.describe('Negative: Add $29.99 item to checkout', () => {
  test('item not added when button not clicked', async ({ page }) => {
    await page.goto('/inventory.html');
    await expect(page.locator('[data-test="inventory-item"]')).toContainText('$29.99');
    await openCart(page);
    await expect(page.locator('.cart_quantity')).toHaveCount(0);
    await expect(page.locator('.cart_total')).toContainText('0.00');
  });

  test('network failure after add prevents cart persistence', async ({ page, context }) => {
    await page.goto('/inventory.html');
    await addFirstItemByPrice(page, '29.99');
    await context.setOffline(true);
    await openCart(page);
    await expect(page.locator('.cart_quantity')).toHaveCount(0);
    await context.setOffline(false);
    await page.reload();
    await expect(page.locator('.cart_quantity')).toHaveCount(0);
  });

  test('rapid double-click adds only one item', async ({ page }) => {
    await page.goto('/inventory.html');
    const btn = page.locator('[data-test="inventory-item"]')
                   .filter({ hasText: '$29.99' })
                   .locator('[data-test="add-to-cart-btn"]');
    await btn.dblclick();
    await openCart(page);
    await expect(page.locator('.cart_quantity')).toHaveCount(1);
  });

  test('wrong item in cart does not show $29.99', async ({ page }) => {
    await page.goto('/inventory.html');
    await addFirstItemByPrice(page, '15.99'); // add cheaper item
    await openCart(page);
    const total = await cartTotal(page);
    expect(total).not.toBe(29.99);
  });
});
