utilsCode:
export async function login(page, username, password) {
  await page.fill('[data-test="username"]', username);
  await page.fill('[data-test="password"]', password);
  await page.click('[data-test="login-button"]');
}

export async function logout(page) {
  await page.click('#react-burger-menu-btn');
  await page.click('[data-test="logout-sidebar-link"]');
}

export async function getErrorMessage(page) {
  return await page.textContent('[data-test="error"]');
}

playwrightCode:
import { test, expect } from '@playwright/test';
import { login, logout, getErrorMessage } from './utils.js';

test.describe('Login flow – negative path', () => {
  test('Login with wrong password fails and keeps user on login page', async ({ page }) => {
    await page.goto('https://www.saucedemo.com/');
    await expect(page).toHaveTitle(/Swag Labs/);

    // positive path to ensure credentials work
    await login(page, 'standard_user', 'secret_sauce');
    await expect(page).toHaveURL('https://www.saucedemo.com/inventory.html');
    await expect(page.locator('[data-test="title"]')).toHaveText('Products');

    // logout to return to login page
    await logout(page);

    // negative path – wrong password
    await login(page, 'standard_user', 'bad_pass');
    const msg = await getErrorMessage(page);
    expect(msg).toContain('Username and password do not match any user in this service');
    await expect(page).toHaveURL('https://www.saucedemo.com/');
  });
});
