import { test, expect } from '@playwright/test';
import { login } from './utils.js';

test('Login with standard_user should succeed', async ({ page }) => {
  await page.goto('https://www.saucedemo.com/');
  await expect(page).toHaveURL(/.*saucedemo\.com/);

  await login(page, 'standard_user', 'secret_sauce');

  await expect(page).toHaveURL('https://www.saucedemo.com/inventory.html');
  await expect(page.locator('[data-test="inventory-container"]')).toBeVisible();
});

utilsCode:
export async function login(page, username, password) {
  if (username !== undefined) await page.fill('[data-test="username"]', username);
  if (password !== undefined) await page.fill('[data-test="password"]', password);
  await page.click('[data-test="login-button"]');
}

export async function logout(page) {
  await page.click('#react-burger-menu-btn');
  await page.click('[data-test="logout-sidebar-link"]');
}

export async function addFirstItemToCart(page) {
  await page.click('[data-test="add-to-cart-sauce-labs-backpack"]');
}

export async function removeFirstItemFromCart(page) {
  await page.click('[data-test="remove-sauce-labs-backpack"]');
}

playwrightCode:
import { test, expect } from '@playwright/test';
import { login } from './utils.js';

test.describe('Login - Negative Paths', () => {
  test('should fail with invalid credentials', async ({ page }) => {
    await page.goto('https://www.saucedemo.com/');
    await login(page, 'wrong_user', 'wrong_pass');
    await expect(page.locator('[data-test="error"]')).toContainText('Username and password do not match');
    await expect(page).toHaveURL('https://www.saucedemo.com/');
  });

  test('should fail with empty username', async ({ page }) => {
    await page.goto('https://www.saucedemo.com/');
    await login(page, '', 'secret_sauce');
    await expect(page.locator('[data-test="error"]')).toContainText('Username is required');
    await expect(page).toHaveURL('https://www.saucedemo.com/');
  });

  test('should fail with empty password', async ({ page }) => {
    await page.goto('https://www.saucedemo.com/');
    await login(page, 'standard_user', '');
    await expect(page.locator('[data-test="error"]')).toContainText('Password is required');
    await expect(page).toHaveURL('https://www.saucedemo.com/');
  });

  test('should fail with locked out user', async ({ page }) => {
    await page.goto('https://www.saucedemo.com/');
    await login(page, 'locked_out_user', 'secret_sauce');
    await expect(page.locator('[data-test="error"]')).toContainText('Sorry, this user has been locked out');
    await expect(page).toHaveURL('https://www.saucedemo.com/');
  });
});
