playwrightCode:
import { test, expect } from '@playwright/test';
import { login } from './utils.js';

test('successful login with standard_user', async ({ page }) => {
  await page.goto('https://www.saucedemo.com/');
  await expect(page).toHaveTitle(/Swag Labs/);

  await login(page, 'standard_user', 'secret_sauce');

  await expect(page).toHaveURL('https://www.saucedemo.com/inventory.html');
  await expect(page.locator('[data-test="title"]')).toHaveText('Products');
  await expect(page.locator('[data-test="inventory-container"]')).toBeVisible();
});

utilsCode:
export async function fillText(page, selector, text) {
  await page.fill(selector, text);
}

export async function clickButton(page, selector) {
  await page.click(selector);
}

export async function assertUrlIsNot(page, url) {
  await page.waitForURL(url => url.href !== url);
}

export async function assertVisible(page, selector) {
  await page.waitForSelector(selector, { state: 'visible' });
}

playwrightCode:
import { test, expect } from '@playwright/test';
import { fillText, clickButton, assertUrlIsNot, assertVisible } from './utils.js';

test.describe('Login - Negative path', () => {
  test('standard_user with secret_sauce should NOT reach inventory page', async ({ page }) => {
    await page.goto('https://www.saucedemo.com/');
    await expect(page).toHaveTitle(/Swag Labs/);

    await fillText(page, 'input[placeholder="Username"]', 'standard_user');
    await fillText(page, 'input[placeholder="Password"]', 'secret_sauce');
    await clickButton(page, 'input[type="submit"][value="Login"]');

    await assertUrlIsNot(page, 'https://www.saucedemo.com/inventory.html');
    await assertVisible(page, '[data-test="error"]');

    const errorText = await page.textContent('[data-test="error"]');
    expect(errorText).toContain('Epic sadface');

    const userValue = await page.inputValue('input[placeholder="Username"]');
    expect(userValue).toBe('standard_user');

    const passValue = await page.inputValue('input[placeholder="Password"]');
    expect(passValue).toBe('secret_sauce');

    await expect(page.locator('input[type="submit"][value="Login"]')).toBeEnabled();
  });
});
