export async function addFirstItemToCart(page) {
  await page.locator('.inventory_item button').first().click();
  await page.waitForSelector('.shopping_cart_badge');
}

export async function goToCheckout(page) {
  await page.locator('.shopping_cart_container').click();
  await page.waitForURL('**/cart.html');
}

export async function mockInventoryPrice(page, price) {
  await page.route('**/inventory', async (route) => {
    const json = await route.fetch().then(r => r.json());
    json[0].price = price;
    await route.fulfill({ json });
  });
}

export async function blockAddToCart(context, block) {
  await context.route('**/add-to-cart', (route) => {
    if (block) route.abort('failed'); else route.continue();
  });
}

export async function hideCartIcon(page) {
  await page.addStyleTag({ content: '.shopping_cart_container { display:none !important; }' });
}

