import { test, expect } from '@playwright/test';
import { addFirstItemToCart, goToCheckout, mockInventoryPrice, blockAddToCart, hideCartIcon } from './utils.js';

test.describe('Negative scenarios - Add item to checkout', () => {
  test.beforeEach(async ({ page }) => {
    await page.goto('/inventory.html');
  });

  test('Network failure when adding to cart', async ({ page, context }) => {
    await blockAddToCart(context, true);
    await addFirstItemToCart(page);
    await expect(page.locator('.shopping_cart_badge')).not.toBeVisible();
    await blockAddToCart(context, false);
    await addFirstItemToCart(page);
    await expect(page.locator('.shopping_cart_badge')).toHaveText('1');
  });

  test('Invalid price format', async ({ page }) => {
    await mockInventoryPrice(page, '29.99');
    await addFirstItemToCart(page);
    await goToCheckout(page);
    await expect(page.locator('.inventory_item_price')).not.toHaveText('$29.99');
  });

  test('Cart icon missing', async ({ page }) => {
    await hideCartIcon(page);
    await addFirstItemToCart(page);
    const cart = page.locator('.shopping_cart_container');
    await expect(cart).not.toBeVisible();
    const navPromise = page.waitForNavigation({ timeout: 2000 }).catch(() => {});
    await cart.click();
    expect(page.url()).toContain('/inventory.html');
  });

  test('Double-click race condition', async ({ page }) => {
    const btn = page.locator('.inventory_item button').first();
    await btn.dblclick();
    await expect(page.locator('.shopping_cart_badge')).toHaveText('1');
  });

  test('Item removed before checkout', async ({ page }) => {
    await addFirstItemToCart(page);
    await page.locator('.inventory_item button').first().click();
    await goToCheckout(page);
    await expect(page.locator('.cart_item')).not.toBeVisible();
  });

  test('Price mismatch', async ({ page }) => {
    await mockInventoryPrice(page, '$39.99');
    await addFirstItemToCart(page);
    await goToCheckout(page);
    await expect(page.locator('.inventory_item_price')).not.toHaveText('$29.99');
  });
});
