/**
 * utils.js â€“ reusable helper functions for Swag Labs tests
 */

/**
 * Perform login on Swag Labs
 * @param {import('@playwright/test').Page} page
 * @param {string} username
 * @param {string} password
 */
export async function login(page, username, password) {
  await page.fill('[data-test="username"]', username);
  await page.fill('[data-test="password"]', password);
  await page.click('[data-test="login-button"]');
  await page.waitForURL('**/inventory.html');
}

/**
 * Get the displayed error message on the login page (if any)
 * @param {import('@playwright/test').Page} page
 * @returns {Promise<string>}
 */
export async function getLoginError(page) {
  const errorLocator = page.locator('[data-test="error"]');
  if (!(await errorLocator.isVisible())) return '';
  return errorLocator.textContent();
}

playwrightCode:
import { test, expect } from '@playwright/test';
import { login } from './utils.js';

test.describe('Login flow', () => {
  test('successful login with standard_user', async ({ page }) => {
    await page.goto('https://www.saucedemo.com/');
    await login(page, 'standard_user', 'secret_sauce');
    await expect(page).toHaveURL('https://www.saucedemo.com/inventory.html');
  });
});
